import React from "react";
import { useHistory } from "react-router-dom";

export default function Trainer({ user, workouts, setWorkouts }) {
  const history = useHistory();

  if (!user) history.push("/");

  const startBtn = () => {
    // ищем первую тренировку, которую пользователь не прошел(where success === false)
    const findId = workouts.find((workout) => workout.success === false);
    // если такая тренировка нашлась - переходим на ее страницу
    if (findId) history.push(`/training_page/${findId.id}`);
    // если не нашлась - значит пользователь прошел все тренировки и выводим сообщение об этом
    else alert("have some rest! :)");
  };

  const buttons = workouts.map((value, key) => {
    return (
      <button
        // если человек прошел это занятие, то value.success становится true 
        disabled={value.success}
        key={key}
        onClick={() => history.push(`/training_page/${value.id}`)}
      >
        Workout N.O. {value.id} <br /> {value.km} Km
      </button>
    );
  });

  return (
    <div>
      <h1>Trainer</h1>
      <br />
      <button onClick={() => startBtn()}>Start</button>
      {buttons}
    </div>
  );
}
