import "./App.css";
import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import Home from "./pages/Home";
import EnterData from "./pages/EnterData";
import Ready from "./pages/Ready";
import Trainer from "./pages/Trainer";
import TrainingPage from "./pages/TrainingPage";

function App() {
  // состояние нашего юзера, если он не вошел в сисетму - значение false, default - false
  const [user, setUser] = useState(false);
  // хранится массив из объектов с нашими тренировками(рассчитывается на странице Ready)
  const [workouts, setWorkouts] = useState(false);
  return (
    <BrowserRouter>
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => <Home user={user} setUser={setUser} />}
          />
          <Route
            exact
            path="/EnterData"
            render={() => <EnterData user={user} setUser={setUser} />}
          />
          <Route
            exact
            path="/ready"
            render={() => (
              <Ready setWorkouts={setWorkouts} user={user} setUser={setUser} />
            )}
          />
          <Route
            exact
            path="/trainer"
            render={() => <Trainer workouts={workouts} user={user} />}
          />
          <Route
            exact
            // если в path будет задан параметр через ":", то он появится в параметре { match }
            path="/training_page/:id"
            // здесь уже получчаем сам параметр ({ match }
            render={({ match }) => (
              <TrainingPage
                workouts={workouts}
                setWorkouts={setWorkouts}
                id={match.params.id}
              />
            )}
          />
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
