import "./App.css";
import React, { useState } from "react";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import Home from "./pages/Home";
import EnterData from "./pages/EnterData";
import Ready from "./pages/Ready";
import Trainer from "./pages/Trainer";
import TrainingPage from "./pages/TrainingPage";

const setWork = (user, setWorkouts) => {
  const object = [];
  for (let i = 0; i < user.workouts; i++) {
    let init_km = (user.trainingYears * 5) / user.workouts;
    let additional_km = init_km * (0.15 * i);

    object.push({
      id: i + 1,
      km: (init_km + additional_km).toFixed(1),
      success: false,
    });
    //(user.years * 5) / user.workouts;
  }
  setWorkouts(object);
  console.log(user);
};

function App() {
  const [user, setUser] = useState({
    number: "123456789",
    name: "john smith",
    gender: "male",
    workouts: 5,
    trainingYears: 5,
  });
  const [workouts, setWorkouts] = useState(false);
  if (user && !workouts) setWork(user, setWorkouts);
  return (
    <BrowserRouter>
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => <Home user={user} setUser={setUser} />}
          />
          <Route
            exact
            path="/EnterData"
            render={() => <EnterData user={user} setUser={setUser} />}
          />
          <Route exact path="/ready" render={() => <Ready />} />
          <Route
            exact
            path="/trainer"
            render={() => (
              <Trainer
                workouts={workouts}
                setWorkouts={setWorkouts}
                user={user}
              />
            )}
          />
          <Route
            exact
            path="/training_page/:id"
            render={({ match }) => (
              <TrainingPage
                workouts={workouts}
                setWorkouts={setWorkouts}
                id={match.params.id}
              />
            )}
          />
        </Switch>
      </div>
    </BrowserRouter>
  );
}

export default App;
