import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";

export default function Trainer({ user, workouts, setWorkouts }) {
  // years * 5 / workouts
  const history = useHistory();

  if (!user) history.push("/");

  useEffect(() => {
    alert("useEffect");
    if (!workouts.length || !workouts) {
      alert("updating");
      const object = [];
      for (let i = 0; i < user.workouts; i++) {
        let init_km = (user.trainingYears * 5) / user.workouts;
        let additional_km = init_km * (0.15 * i);

        object.push({
          id: i + 1,
          km: (init_km + additional_km).toFixed(1),
          success: false,
        });
        //(user.years * 5) / user.workouts;
      }
      console.log(object);
      console.log(user);
      setWorkouts(object);
    }
  }, []);
  console.log(workouts);

  const startBtn = () => {
    const findId = workouts.find((workout) => workout.success === false);
    if (findId) history.push(`/training_page/${findId.id}`);
    else alert("have some rest! :)");
  };

  const buttons = workouts.map((value, key) => {
    return (
      <button
        disabled={value.success}
        key={key}
        onClick={() => history.push(`/training_page/${value.id}`)}
      >
        Workout N.O. {value.id} <br /> {value.km} Km
      </button>
    );
  });

  return (
    <div>
      <h1>Trainer</h1>
      <br />
      <button
        onClick={() => {
          startBtn();
        }}
      >
        {" "}
        Start
      </button>
      {buttons}
    </div>
  );
}
