import React from "react";
import { useHistory } from "react-router-dom";

export default function Ready({ setUser, user, setWorkouts }) {
  const history = useHistory();

  const btnHandler = (btn) => {
    if (btn) {
      // пустой объект для будущих значений
      const object = [];

      // создаем цикл в связи с количеством тренировок, которые выбрал пользователь (user.workouts)
      for (let i = 0; i < user.workouts; i++) {
        // создаем переменную, где вычисляем количество километров
        // по формуле (количество лет * 5 / количество занятий)
        // если количество лет = 0, то задаем изначальное значение в 2 километра
        let init_km =
          +user.trainingYears !== 0
            ? (user.trainingYears * 5) / user.workouts
            : 2;
        // additional_km - дополнительные километры на каждое навое занятие(+15%)
        let additional_km = init_km * (0.15 * i);

        // добавляем информацию о тренировке в массив
        object.push({
          id: i + 1,
          km: (init_km + additional_km).toFixed(1),
          success: false,
        });
        // toFixed(1) убирает дополнительные номера после точки
        // пример: 0ю0
        //(user.years * 5) / user.workouts;
      }

      setWorkouts(object);

      history.push("/trainer");
    } else {
      setUser(false);
      history.push("/");
    }
  };
  return (
    <div>
      <h1>Ready</h1>
      <button onClick={() => btnHandler(true)}>Yes</button>
      <button onClick={() => btnHandler(false)}>No</button>
    </div>
  );
}
